import React, { useState } from 'react';
import Message from './Components/class-msg';
import Hello from './Components/function-msg'

export default function App() {

  const [count, setCount] = useState(0)


  function increament() {
    // setCount(prevCount => prevCount + 1)
    setCount(count => count + 1)

  }

  function decreament() {
    setCount(count => count - 1)
    // setCount(prevCount => prevCount - 1)

  }


  const [isMorning, setMorning] = useState(fal)

  return (
    <div>


      {/* through class components */}
      < Message numbers={count} />
      {/* through function components  */}
      < Hello numbers={count} />

      <button onClick={increament}>Add My VALUE</button>
      <button onClick={decreament}>Subtract My VALUE</button>

      <h1> Good {isMorning ? 'Morning' : 'Night'}</h1>

      <button onClick={setMorning(!isMorning)}>Update Day</button> 
    </div>
  )
}
